import * as THREE from 'three';

import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls.js'; 
import * as dat from 'dat.gui'
console.log(dat)
console.log(OrbitControls)

const gui= new dat.GUI()
const world ={
  plane:{
    width:10,height:10,widthSegments:10,heightSegments:10

  }
}
gui.add(world.plane, 'height',1,20).onChange(generatePlane)
gui.add(world.plane, 'width',1,22).onChange(generatePlane)
gui.add(world.plane, 'widthSegments',1,22).onChange(generatePlane)

gui.add(world.plane, 'heightSegments',1,22).onChange(generatePlane)

function generatePlane() {
  planeMesh.geometry.dispose()
  planeMesh.geometry= new THREE.PlaneGeometry(world.plane.width,world.plane.height,world.plane.widthSegments,world.plane.heightSegments)

  console.log(planeMesh.geometry.attributes.position.array)
const {array}= planeMesh.geometry.attributes.position
for(let i=0;i<array.length; i +=3){

  const x= array[i]
  const y= array[i+1]
  const z= array[i+2]

  array[i+2]= z + Math.random()

}
} 





const scene = new THREE.Scene();
const camera= new THREE.PerspectiveCamera(75,window.innerWidth/window.innerHeight,0.1,1000);
const renderer= new THREE.WebGLRenderer();
//console.log(scene);
//console.log(camera);
//console.log(renderer);
renderer.setSize(window.innerWidth, window.innerHeight);
renderer.setPixelRatio(devicePixelRatio);
document.body.appendChild(renderer.domElement);

camera.position.z =5
const material = new THREE.MeshBasicMaterial({color: 0x00ff00})

const PlaneGeometry = new THREE.PlaneGeometry(5,5,10,10)

const PlaneMaterial = new THREE.MeshPhongMaterial({color: 0xff0000, side: THREE.DoubleSide,
flatShading: true
})

const planeMesh =new THREE.Mesh(PlaneGeometry,PlaneMaterial)
scene.add(planeMesh)

console.log(planeMesh.geometry.attributes.position.array)
const {array}= planeMesh.geometry.attributes.position
for(let i=0;i<array.length; i +=3){

  const x= array[i]
  const y= array[i+1]
  const z= array[i+2]

  array[i+2]= z + Math.random()


}

const light= new THREE.DirectionalLight(0xffffff,1)
light.position.set(0,0,1)
scene.add(light)




function animate() {
  requestAnimationFrame(animate);
  renderer.render(scene, camera);

  // planeMesh.rotation.x +=0.01
  //planeMesh.rotation.y += 0.01
}
animate();
